import {v4 as uuidv4} from 'uuid';
import { ChatBoxModel } from '../models/chatbox';

const makeName = (name, friend) => {
  return [name, friend].sort().join('_');
}

const checkOutChatBox = async(name1, name2) => {
  let chatBoxName = [name1, name2].sort().join('_');
  let box = await ChatBoxModel.findOne({ name: chatBoxName });
  if (!box){
    // 要先有users ObjectId.
    console.log('ChatBoxModel不存在 -> 建立ChatBoxModel');
    box = await new ChatBoxModel({ name: chatBoxName }).save();
    // await myId.updateOne({"$push": {chatBoxes: box._id}});
    // await friendId.updateOne({"$push": {chatBoxes: box._id}});
  }
  else{
      console.log("Find Current Chat Box", box.name);
  }

  console.log('Validation of chat box:', (box));
  return box;
}

const Mutation = {
  createChatBox: async(parent, {name1, name2}, {ChatBoxModel}) => {
    let box = await checkOutChatBox(name1, name2);
    return box;
  },

  createMessage: async(parent, { name, to, body }, { pubsub }) => {
    const chatBox = await checkOutChatBox(name, to);
    const newMsg = { sender: name, body: body };
    chatBox.messages.push(newMsg);
    await chatBox.save();

    const chatBoxName = makeName(name, to);
    pubsub.publish(`chatbox ${chatBoxName}`, { message: newMsg });

    return newMsg;
  }

